<style>
  
  article {
    background-color: var(--grey);
    display: flex;
    padding: var(--spacing-20);
    gap: 20px;
    border-radius: var(--border-radius-sm);
    justify-content: start;
    align-items: center;
    flex-direction: row-reverse;
    text-align: end;
  }

  .article_container {
    display: grid;
    padding: var(--spacing-20);
    gap: 20px;
  }

  @media (min-width: 810px) {
  
    article {
      width: 810px;
    }

    .article_container {
      justify-content: center;
    }
    


  }


  @media (min-width: 1200px) {
  
    .article_container {
    display: flex;
    padding: 0px;
    gap: 20px;
    justify-content: center;
  }

  article {
    width: 300px;
    height: 400px;
    display: grid;
    place-items: center;
    justify-content: space-evenly;
    text-align: center;
  }
}



</style>
